var options = [];

if(msg.payload.length > 0){
    msg.payload.forEach(d => {
        var obj = {};
        obj[d.name || d.serial] = d.serial;
        options.push(obj)
    })

    msg.payload = msg.payload[0].serial;
}

msg.options = options;
return msg;

var devices = flow.get('devicesBySerial');
var serial = msg.payload;
msg.serial = serial;
if(serial.length > 0){
   var device = devices[serial];
   msg.networkId = device.networkId;
   msg.device = device;
   msg.payload = device;
}else{
    msg.payload = "";
}


return msg;

